// const first_name = 
return {
  "email_subject": "You're Invited to the Next Stage of Our Hiring Process!",
  "email_body": `<p>Hi $json.fields.Email,</p> <p>We are thrilled to inform you that you've been shortlisted for the next stage of our recruitment process!</p> <p>To proceed, please schedule your interview at a time that works best for you:</p> <p><a href='https://calendly.com/victoranosunkvic' target='_blank'>Book Your Interview Slot</a></p> <p>We look forward to speaking with you soon!</p> <p>Best regards,<br>The Recruitment Team</p>`
}

// Extract the "Years of Experience" field value
const experienceText = $json['Years of Experience in Copywriting'];
// Use regex to extract the first number (e.g., "3" from "3–5 years")
const experience = parseInt(experienceText.match(/\d+/)?.[0]) || 0;

// Extract "Copywriting Skills" and convert to a readable string
const copywritingSkillsArray = $json['Copywriting Skills'] || [];
const copywritingSkills = copywritingSkillsArray.join(', ');

// Add the "Status" field with the value "Applied"
const status = (experience > 0 && copywritingSkillsArray.length > 0) ? "Shortlisted" : "Not Qualified";

// Return the combined data including the new "Status" field
return { 
    experience, 
    copywritingSkills, 
    status 
};
